package com.class2;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			// declare 2 int variables and the perform:
		//addition, subtraction, multiplication, division
		
		int a=30;
		int b=15;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);

		int sum=a+b;
		int sub=a-b;
		int	mul=a*b;
		int div= a/b;
		System.out.println(sum + "\n" + sub + "\n" + mul + "\n" + div);
		
		System.out.println("The sum of 2 numbers is " + sum + "\n" + "The substraction of 2 numbers is " + sub + "\n" +
		"The multiplication of 2 numbers is " + mul + "\n" + "The division of 2 numbers is " + div) ;
				
	}

}
