package com.class3;

public class TemperatureCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=32;
		
		if (b>=a) {
			System.out.println("Water will freeze with temperature " + a +"F");
		}else {
			System.out.println("Water will NOT freeze with temperature " + b+"F");
		}
	}

}
